void copyMyDataToDevice(MyData* hostData, MyData*& devData) {
    // Step 1: Allocate memory for the struct on the device
    cudaMalloc(&devData, sizeof(MyData));

    // Step 2: Copy the struct's *value* members (like 'size')
    cudaMemcpy(devData, hostData, sizeof(MyData), cudaMemcpyHostToDevice);

    // Step 3: For each pointer member, allocate GPU memory and copy the data
