        int* devDataPtr;
        cudaMalloc(&devDataPtr, hostData->size * sizeof(int));
        cudaMemcpy(devDataPtr, hostData->data, hostData->size * sizeof(int), cudaMemcpyHostToDevice);
        cudaMemcpy(&(devData->data), &devDataPtr, sizeof(int*), cudaMemcpyHostToDevice);
