// Countdown Timer Component for Vocabulary App
class VocabularyCountdownTimer {
  constructor(elementId, startDate, endDate, onCompleteCallback = null) {
    this.element = document.getElementById(elementId);
    this.startTime = new Date(startDate).getTime();
    this.endTime = new Date(endDate).getTime();
    this.onComplete = onCompleteCallback;
    this.intervalId = null;

    this.init();
  }

  init() {
    const now = Date.now();

    if (now < this.startTime) {
