    int* data;
    float* values;
    int size;
};

// Helper function to allocate and copy struct to device
DataStruct* createDeviceStruct(const DataStruct& hostStruct) {
    DataStruct* deviceStruct;
    DataStruct* tempHostStruct = new DataStruct;

    // Allocate device struct
    cudaMalloc(&deviceStruct, sizeof(DataStruct));

    // Allocate and copy each pointer member
    if (hostStruct.data != nullptr) {
        cudaMalloc(&tempHostStruct->data, hostStruct.size * sizeof(int));
        cudaMemcpy(tempHostStruct->data, hostStruct.data, 
                   hostStruct.size * sizeof(int), cudaMemcpyHostToDevice);
    }

    if (hostStruct.values != nullptr) {
        cudaMalloc(&tempHostStruct->values, hostStruct.size * sizeof(float));
        cudaMemcpy(tempHostStruct->values, hostStruct.values, 
                   hostStruct.size * sizeof(float), cudaMemcpyHostToDevice);
    }

    tempHostStruct->size = hostStruct.size;

    // Copy the struct with device pointers
    cudaMemcpy(deviceStruct, tempHostStruct, sizeof(DataStruct), cudaMemcpyHostToDevice);

    delete tempHostStruct;
    return deviceStruct;
}
```

### 2. **Use Unified Memory (Simpler Alternative)**

For CUDA 6.0+, consider **Unified Memory** (`cudaMallocManaged`) which simplifies pointer management:

```cuda
__managed__ struct MyStruct* managedStruct;

// Or allocate managed memory
cudaMallocManaged(&managedStruct, sizeof(MyStruct));
cudaMallocManaged(&managedStruct->data, size * sizeof(int));
```

Unified Memory is accessible from both host and device, eliminating manual deep copies.

### 3. **For Nested Structs with Pointers**

If you have structs containing structs with pointers (nested hierarchy), you need to recursively allocate:

```cuda
// For nested pointers, work from the bottom up
// 1. Allocate leaf nodes first
// 2. Update parent pointers
// 3. Copy parent structs
// 4. Continue up the hierarchy
```

### 4. **Cleanup Pattern**

Always clean up in reverse order:
```cuda
void cleanupDeviceStruct(DataStruct* deviceStruct) {
    DataStruct tempHostStruct;
    cudaMemcpy(&tempHostStruct, deviceStruct, sizeof(DataStruct), cudaMemcpyDeviceToHost);

    cudaFree(tempHostStruct.data);
    cudaFree(tempHostStruct.values);
    cudaFree(deviceStruct);
}
```

