#include <cuda_runtime.h>
#include <cstdio>
#include <cstdlib>

// Simple CUDA error checking
#define CUDA_CHECK(call) do { \
    cudaError_t err__ = (call); \
    if (err__ != cudaSuccess) { \
        fprintf(stderr, "CUDA error: %s (code %d) at %s:%d\n", \
                cudaGetErrorString(err__), (int)err__, __FILE__, __LINE__); \
        std::exit(EXIT_FAILURE); \
    } \
} while (0)

// Example struct: adapt fields to your project.
// Important: These pointers must point to device memory to be usable in kernels.
struct DeviceStruct {
    float* values;     // length n
    int*   indices;    // length n
    size_t n;
};

// Example kernel: reads/writes via struct pointers.
__global__ void compute_by_value(DeviceStruct ds) {
    size_t i = blockIdx.x * blockDim.x + threadIdx.x;
    if (i < ds.n) {
        // Example: add indices[i] to values[i]
        ds.values[i] += static_cast<float>(ds.indices[i]);
    }
}

// Same kernel but taking a pointer to a struct allocated on device.
__global__ void compute_by_pointer(DeviceStruct* ds) {
    size_t i = blockIdx.x * blockDim.x + threadIdx.x;
    if (i < ds->n) {
        ds->values[i] *= 2.0f; // Example second operation
    }
}

// Helper: deep-copy host buffers into device allocations and return a "device-shadow" struct.
// The returned struct has device pointers but resides in host memory; safe to pass by value to kernels.
static DeviceStruct make_device_shadow(const float* h_values, const int* h_indices, size_t n) {
    DeviceStruct shadow{};
    shadow.n = n;

    // Allocate device memory for each pointer member and copy data.
    CUDA_CHECK(cudaMalloc(reinterpret_cast<void**>(&shadow.values), n * sizeof(float)));
    CUDA_CHECK(cudaMemcpy(shadow.values, h_values, n * sizeof(float), cudaMemcpyHostToDevice));

    CUDA_CHECK(cudaMalloc(reinterpret_cast<void**>(&shadow.indices), n * sizeof(int)));
    CUDA_CHECK(cudaMemcpy(shadow.indices, h_indices, n * sizeof(int), cudaMemcpyHostToDevice));

    return shadow;
}

// Helper: allocate a device struct pointer and copy the "device-shadow" struct into device memory.
// Use this if you need a DeviceStruct* on device (e.g., store it globally or pass by pointer).
static DeviceStruct* copy_struct_header_to_device(const DeviceStruct& shadow) {
    DeviceStruct* d_struct = nullptr;
    CUDA_CHECK(cudaMalloc(reinterpret_cast<void**>(&d_struct), sizeof(DeviceStruct)));
    CUDA_CHECK(cudaMemcpy(d_struct, &shadow, sizeof(DeviceStruct), cudaMemcpyHostToDevice));
    return d_struct;
}

// Helper: read back a device struct header into host memory.
// Useful in cleanup to know which device pointers to free.
static DeviceStruct copy_struct_header_to_host(DeviceStruct* d_struct) {
    DeviceStruct h_copy{};
    CUDA_CHECK(cudaMemcpy(&h_copy, d_struct, sizeof(DeviceStruct), cudaMemcpyDeviceToHost));
    return h_copy;
}

// Helper: free all device allocations associated with the struct.
// If you only passed by value and never allocated a device struct header, call free_device_shadow().
// If you allocated a device struct header (DeviceStruct*), call free_device_struct_pointer().
static void free_device_shadow(DeviceStruct& shadow) {
    if (shadow.values) CUDA_CHECK(cudaFree(shadow.values));
    if (shadow.indices) CUDA_CHECK(cudaFree(shadow.indices));
    shadow.values = nullptr;
    shadow.indices = nullptr;
    shadow.n = 0;
}

static void free_device_struct_pointer(DeviceStruct* d_struct) {
    if (!d_struct) return;
    // First get the device-side pointers from the header
    DeviceStruct h_copy = copy_struct_header_to_host(d_struct);
    // Free the pointee buffers
    if (h_copy.values)  CUDA_CHECK(cudaFree(h_copy.values));
    if (h_copy.indices) CUDA_CHECK(cudaFree(h_copy.indices));
    // Then free the header itself
    CUDA_CHECK(cudaFree(d_struct));
}

// Optional: Unified Memory variant for simpler ownership.
// Note: You must allocate ALL pointer members with cudaMallocManaged, not host malloc/new.
// Then you can use the struct on both host and device without explicit cudaMemcpy.
static DeviceStruct make_unified_struct(size_t n) {
    DeviceStruct ums{};
    // Allocate the struct header in unified memory OR keep it as a host object.
    // Commonly we just keep header on host and allocate pointees as managed.
    ums.n = n;
    CUDA_CHECK(cudaMallocManaged(reinterpret_cast<void**>(&ums.values), n * sizeof(float)));
    CUDA_CHECK(cudaMallocManaged(reinterpret_cast<void**>(&ums.indices), n * sizeof(int)));
    // After populating ums.values and ums.indices on host, consider prefetching to the device:
    int dev = 0;
    CUDA_CHECK(cudaGetDevice(&dev));
    CUDA_CHECK(cudaMemPrefetchAsync(ums.values,  n * sizeof(float), dev));
    CUDA_CHECK(cudaMemPrefetchAsync(ums.indices, n * sizeof(int),   dev));
    CUDA_CHECK(cudaDeviceSynchronize());
    return ums;
}

int main() {
    // Example host data (replace with your actual initialization).
    const size_t n = 1 << 20;
    float* h_values  = static_cast<float*>(std::malloc(n * sizeof(float)));
    int*   h_indices = static_cast<int*>(std::malloc(n * sizeof(int)));
    if (!h_values || !h_indices) {
        fprintf(stderr, "Host allocation failure\n");
        return EXIT_FAILURE;
    }
    for (size_t i = 0; i < n; ++i) {
