#### Sample Implementation Code:
```javascript
function createCountdown(Ele, StartDate, EndDate, EndFunc) {
  const start = new Date(StartDate).getTime();
  const end = new Date(EndDate).getTime();
  const element = document.getElementById(Ele);

  if (!element) {
    console.error("Element not found:", Ele);
    return;
  }

  if (end <= start) {
    console.error("EndDate must be after StartDate");
    return;
  }

  const updateTimer = () => {
    const now = Date.now();
    if (now < start) {
      element.innerHTML = "Countdown starts at " + StartDate;
      return;
    }
    const remaining = end - now;
    if (remaining <= 0) {
      element.innerHTML = "Countdown ended!";
      if (EndFunc) EndFunc();
      return;
    }

    const days = Math.floor(remaining / (1000 * 60 * 60 * 24));
    const hours = Math.floor((remaining % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutes = Math.floor((remaining % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((remaining % (1000 * 60)) / 1000);

    element.innerHTML = `${days}d ${hours}h ${minutes}m ${seconds}s remaining`;
  };

  updateTimer(); // Initial update
  setInterval(updateTimer, 1000); // Update every second
}

